/**
 * “styxprg” (add before, below, or wrap selected content with a paragraph) plugin
 * for TinyMCE 6.8.4 in Serendipity Styx
 */
tinymce.PluginManager.add('styxPrg',(editor,url)=>{editor.ui.registry.addIcon('divcode','<svg height="24" width="24" viewBox="0 0 24 24"><path d="M12.89,3L14.85,3.4L11.11,21L9.15,20.6L12.89,3M19.59,12L16,8.41V5.58L22.42,12L16,18.41V15.58L19.59,12M1.58,12L8,5.58V8.41L4.41,12L8,15.58V18.41L1.58,12Z" /></svg>');editor.ui.registry.addButton('styxprg',{text:'P',icon:'divcode',tooltip:"Add before or below cursor, or wrap marked text with a paragraph",onAction:()=>{let cnt=editor.selection.getContent({format:'html'});editor.insertContent(`<p>${cnt}</p>`,{format:'html'});}});editor.ui.registry.addMenuItem('styxPrg',{icon:'divcode',text:'add {p} container',onAction:()=>{let cnt=editor.selection.getContent({format:'html'});editor.insertContent(`<p>${cnt}</p>`, {format:'html'});}});return{getMetadata:()=>({name:'Paracon',url:url})};});